'      EXECUTE IMMEDIATE ''SELECT ''||l_seq.val||'' FROM DUAL'' INTO l_curr;',
'      dbms_output.put_line(''MAX: ''||l_max||'' CURR: ''||l_curr);    ',
'      EXIT WHEN l_curr>=l_max;',
'    END LOOP;',
'  END LOOP;',
'END;',
'/',
''))
