'  END IF;',
'  RETURN l_json;',
'END readschema;',
'',
'/*',
' * Called when the plugin is to be rendered',
' */',
'FUNCTION render_region(p_region              IN apex_plugin.t_region,',
'                       p_plugin              IN apex_plugin.t_plugin,',
'                       p_is_printer_friendly IN BOOLEAN)',
